import axios from 'axios';
import styled from 'styled-components';
import { useRef, useState } from 'react';

const api = `http://api.datamuse.com/words?rel_rhy=`;

const StyledDiv = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  background-color: #f07070;
  width: 40%;
  border-radius: 1rem;
  padding: 3rem;

  h1 {
    font-family: 'Work Sans', sans-serif;
    color: #4a3da0;
    font-weight: 100;
    font-size: 3.2rem;
    margin-top: 2rem;
    text-align: center;
  }
  input {
    margin-top: 1rem;
    height: 4rem;
    font-size: 1.6rem;
    background-color: rgba(255, 255, 255, 0.153);
    text-align: center;
    border-radius: 0.5rem;
    border: 0.5px solid #4a3da0;
    outline: none;
    color: #4a3da0;

    ::placeholder {
      color: rgba(74, 61, 160, 0.517);
    }
  }
  button {
  }
`;

const StyledButton = styled.button`
  padding: 1.6rem;
  width: 40%;
  color: #4a3da0;
  margin-top: 2rem;
  background-color: #70f094;
  outline: none;
  border: 1px solid #70f094;
  border-radius: 5rem;
  font-size: 1.6rem;
  &:active {
    background-color: rgba(112, 240, 148, 0.564);
    transition: 0.1s all ease-in-out;
  }
`;

const StyledOutput = styled.div`
  height: 400px;
  width: 100%;
  margin-top: 2rem;
  background-color: slategray;
  border: 1 px solid darkblue;
  color: white;
  padding: 2rem;
  font-size: 1.2rem;
  border-radius: 0.5rem;
`;

const Input = () => {
  const [word, setWord] = useState('');

  const userInput = new useRef(null);

  const buttonClickHandler = () => {
    findRhyme(word);
  };

  const inputKeyPress = e => {
    if (e.key == 'Enter') {
      findRhyme(word);
    } else {
      setWord(e.target.value);
    }
  };

  const findRhyme = async word => {
    //   const results = await axios(`${api}'${word}'`)
    //   console.log(results)
    const result = await axios(
      'https://api.datamuse.com/words?rel_rhy=forgetful'
    );
    console.log(result);
  };

  return (
    <StyledDiv>
      <h1>Time to find a Rhyme </h1>
      <input
        type='text'
        placeholder='type a word...'
        autoCorrect='false'
        autoComplete='false'
        spellCheck='false'
        ref={userInput}
        onKeyPress={inputKeyPress}
      ></input>
      <StyledButton onClick={buttonClickHandler}>Find</StyledButton>
      <StyledOutput>
        <p></p>
      </StyledOutput>
    </StyledDiv>
  );
};

export default Input;
